import { Injectable } from "@angular/core"
import type { HttpClient } from "@angular/common/http"
import type { Observable } from "rxjs"
import type { UsuarioListado } from "../models/usuario.model"

@Injectable({
  providedIn: "root",
})
export class UsuarioService {
  private apiUrl = "http://localhost/backend/api/usuarios"

  constructor(private http: HttpClient) {}

  listar(): Observable<{ usuarios: UsuarioListado[] }> {
    return this.http.get<{ usuarios: UsuarioListado[] }>(`${this.apiUrl}/listar.php`)
  }
}
